insert  into `chapters_full`(`tehilim_id`,`tehilim_chap_num`,`tehilim_chap_num_heb`,`tehilim_posuk_num`,`tehilim_posuk_num_heb`,`tehilim_text_nikkud`,`tehilim_text_no_nikkud`,`tehilim_letter_num`,`tehilim_letter_num_heb`) values (3,1,'א',1,'א','אַשְׁרֵי הָאִישׁ אֲשֶׁר לֹא הָלַךְ בַּעֲצַת רְשָׁעִים וּבְדֶרֶךְ חַטָּאִים לֹא עָמָד וּבְמוֹשַׁב לֵצִים לֹא יָשָׁב','אשרי האיש אשר לא הלך בעצת רשעים ובדרך חטאים לא עמד ובמושב לצים לא ישב',NULL,NULL),(4,1,'א',2,'ב','כִּי אִם בְּתוֹרַת יְהוָה חֶפְצוֹ וּבְתוֹרָתוֹ יֶהְגֶּה יוֹמָם וָלָיְלָה','כי אם בתורת יהוה חפצו ובתורתו יהגה יומם ולילה',NULL,NULL),(5,1,'א',3,'ג','וְהָיָה כְּעֵץ שָׁתוּל עַל פַּלְגֵי מָיִם אֲשֶׁר פִּרְיוֹ יִתֵּן בְּעִתּוֹ וְעָלֵהוּ לֹא יִבּוֹל וְכֹל אֲשֶׁר יַעֲשֶׂה יַצְלִיחַ','והיה כעץ שתול על פלגי מים אשר פריו יתן בעתו ועלהו לא יבול וכל אשר יעשה יצליח',NULL,NULL),(6,1,'א',4,'ד','לֹא כֵן הָרְשָׁעִים כִּי אִם כַּמֹּץ אֲ‍שֶׁר תִּדְּפֶנּוּ רוּחַ','לא כן הרשעים כי אם כמץ א‍שר תדפנו רוח',NULL,NULL),(7,1,'א',5,'ה','עַל כֵּן לֹא יָקֻמוּ רְשָׁעִים בַּמִּשְׁפָּט וְחַטָּאִים בַּעֲדַת צַדִּיקִים','על כן לא יקמו רשעים במשפט וחטאים בעדת צדיקים',NULL,NULL),(8,1,'א',6,'ו','כִּי יוֹדֵעַ יְהוָה דֶּרֶךְ צַדִּיקִים וְדֶרֶךְ רְשָׁעִים תֹּאבֵד','כי יודע יהוה דרך צדיקים ודרך רשעים תאבד',NULL,NULL),(9,2,'ב',1,'א','לָמָּה רָגְשׁוּ גוֹיִם וּלְאֻמִּים יֶהְגּוּ רִיק','למה רגשו גוים ולאמים יהגו ריק',NULL,NULL),(10,2,'ב',2,'ב','יִתְיַצְּבוּ מַלְכֵי אֶרֶץ וְרוֹזְנִים נוֹסְדוּ יָחַד עַל יְהוָה וְעַל מְשִׁיחוֹ','יתיצבו מלכי ארץ ורוזנים נוסדו יחד על יהוה ועל משיחו',NULL,NULL),(11,2,'ב',3,'ג','נְנַתְּקָה אֶת מוֹסְרוֹתֵימוֹ וְנַשְׁלִיכָה מִמֶּנּוּ עֲבֹתֵימוֹ','ננתקה את מוסרותימו ונשליכה ממנו עבתימו',NULL,NULL),(12,2,'ב',4,'ד','יוֹשֵׁב בַּשָּׁמַיִם יִשְׂחָק אֲדֹנָי יִלְעַג לָמוֹ','יושב בשמים ישחק אדני ילעג למו',NULL,NULL),(13,2,'ב',5,'ה','אָז יְדַבֵּר אֵלֵימוֹ בְאַפּוֹ וּבַחֲרוֹנוֹ יְבַהֲלֵמוֹ','אז ידבר אלימו באפו ובחרונו יבהלמו',NULL,NULL),(14,2,'ב',6,'ו','וַאֲנִי נָסַכְתִּי מַלְכִּי עַל צִיּוֹן הַר קָדְשִׁי','ואני נסכתי מלכי על ציון הר קדשי',NULL